import './App.css';
import React from 'react';
import SignUp from './SignUp';
import Login from './Login';
import Homescreen from './Homescreen';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';

function App() {
    const location = useLocation(); // This will now work because App is wrapped in Router

    return (
        <>
            {/* Conditionally render the header based on the current location */}
            {location.pathname !== '/Homescreen' && (
                <div className="bg-green-300 h-20 flex justify-center text-4xl py-2">
                    Chat-App
                </div>
            )}
            {/* Define your routes here */}
            <Routes>
                <Route path="/" element={<Login />} />
                <Route path="/SignUp" element={<SignUp />} />
                <Route path="/Homescreen" element={<Homescreen />} />
            </Routes>
        </>
    );
}

// Wrap your App component in BrowserRouter
export default function AppWithRouter() {
    return (
        <Router>
            <App />
        </Router>
    );
}
