import { React, useState, useRef, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
const { io } = require("socket.io-client");


const socket = io.connect("http://localhost:5100")

export default function Homescreen() {
  const location = useLocation();

  const [LeftArrow, setLeftArrow] = useState('false')

  const inputRef = useRef(null);

  const [groupMessages, setGroupMessage] = useState(false);

  const { username, password } = location.state || {};
  const [Showchat, setShowchat] = useState(false)

  const [searchusers, setSearchsUsers] = useState([]);
  const [searchgroupMessages, setsearchGroupMessages] = useState([]);

  const [searchbool, setSearchbool] = useState(false);

  const [mscreen, setMscreen] = useState(true);

  const [senderGroupName, setSenderGroupName] = useState("");


  const clickOnImage = (event) => {
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    if (a.nodeValue == '/leftArrow.png') {
      a.nodeValue = '/searchIcon.png';
      setSearchbool(false);
      setMscreen(true);
      setmessagetext("Type message here");
    }
    else {
      a.nodeValue = '/leftArrow.png';
      setSearchbool(true);
      if (inputRef.current) {
        inputRef.current.focus();
      }

    }
    event.stopPropagation();

  }
  const handleContainerClick = (event) => {
    const target = event.target;
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    const flag = target.classList.contains('searchnot');
  }

  const oninput = async (e) => {
    const value = e.target.value;
    setSearchbool(true);
    if (value.length > 0) {
      const response = await fetch('http://localhost:5010/tofetch', {
        method: 'POST',
        body: JSON.stringify({ "username": value }),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      const data = await response.json();
      const data_users = data.users
      const data_group = data.groupMessages;

      setSearchsUsers(data_users || []);
      setsearchGroupMessages(data_group || []);
    }
    else {
      setSearchsUsers([]);
      setsearchGroupMessages([]);
    }
  }

  useEffect(() => {
    socket.on("receive_message", (data) => {
      console.log("Woring in USe effect");
      console.log(data);
    })
  });



  const startindivimessage = async (e, u2) => {
    setMscreen(false);
    const receiver = username;
    const sender = u2;
    if (sender == receiver) {
      const t1 = receiver + ' (Self)';
      setSenderGroupName(t1);
    }
    else {
      setSenderGroupName(sender);
    }
    setmessagetext("Type message here");
    loadindimess(receiver, sender);
  }
  const loadindimess = async (receiver, sender) => {
    const response = await fetch('http://localhost:5100/tofetchindichat', {
      method: 'POST',
      body: JSON.stringify({ "receiver": receiver, "sender": sender }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const res = await response.json();
    if (res.length == 0) {
      return;
    }
  }

  const [messagetext, setmessagetext] = useState("Type message here");

  // When click on send message arrow
  const messageBox = async (e, flag = true) => {
    e.stopPropagation();
    if (flag == true) {
      if (messagetext == "Type message here") {
        const d = e.target.value;
        const i = d.indexOf('Type message here')
        const m1 = d.substring(i + ('Type message here'.length));
        setmessagetext(m1);
      }
      else if (e.target.value && e.target.value.length > 0) {

        setmessagetext(e.target.value);
      }
      else {

        setmessagetext("Type message here");
      }
    }
    else {
      if (messagetext == "Type message here") {
        e.target.value = "";
        setmessagetext(e.target.value);
      }
    }

  }

  const sendMessage = async (e) => {
    const textToSend = messagetext;
    const receiver = username;
    const sender = senderGroupName;

    if (textToSend.length == 0) {
      return;
    }
    if (groupMessages == false) {
      const identifier1=receiver+'#'+sender;
      const identifier2=sender+'#'+receiver;

      socket.emit('send_message', { identifier1,identifier2,textToSend });
    }

    // const response=await fetch('http://localhost:5100/tofetchindichat',{
    //   method:'post',
    //   body: JSON.stringify({ receiver,sender,textToSend }),
    //   headers: {
    //     'Content-Type': 'application/json'
    //   }
    // });
  }

  return (
    <>
      <div className='flex flex-row h-screen' onClick={handleContainerClick} >
        <div className=' bg-gray-400' style={{ width: '5%', height: '100%' }}>

        </div>
        <div style={{ width: '40%' }}>
          <div className='bg-fixed'>
            <span className='text-3xl font-bold relative top-3 pl-7'>
              Chats
            </span>
            <br />
            <div className='mt-10 flex items-center' id='search_container' style={{ marginLeft: '5%' }}>
              <span className='rounded-l-md bg-gray-200 h-10 pl-3 hover:cursor-pointer' onClick={(e) => clickOnImage(e)}><img src="/searchIcon.png" alt="" style={{ height: '30px', width: '25px' }} id='search_image' className='inline-block h-10 w-3 pt-2' /></span>
              <input type="text" ref={inputRef} placeholder='Search' className=' h-10 rounded-r-md inline bg-gray-200' onChange={(e) => oninput(e)}
                style={{ outline: 'None', width: '90%', paddingLeft: '10%', marginRight: '5%' }} onClick={(e) => clickOnImage(e)} />
            </div>
          </div>
          {searchbool ? (
            <div id='search_panel' className='searchnot overflow-auto' style={{ maxHeight: '480px', overflowY: 'auto' }}>

              {searchusers.length > 0 ? (
                <ul className='text-2xl mt-2  searchnot'>
                  <span className='ml-7 searchnot'>Users:</span>
                  {searchusers.map((user) => (
                    <li key={user._id} onClick={(e) => startindivimessage(e, user.username)} style={{ width: '90%', marginLeft: '5%' }} className='border-8 cursor-pointer mt-2 border-gray-700 pl-2  h-20  w-88  searchnot'>{user.username}</li>
                  ))}
                </ul>
              ) : (
                <ul className='text-2xl mt-2 ml-7 searchnot'> No Users found</ul>)}

              {searchgroupMessages.length > 0 ? (
                <ul className='text-2xl mt-2 ml-7 searchnot'>
                  <span className='ml-7 searchnot'> Group: </span>
                  {searchusers.map((group) => (
                    <li key={group._id} style={{ width: '82%' }} className='border-8 mt-2 border-gray-700 pl-2 searchnot'>{group.groupname}</li>
                  ))}
                </ul>) :
                (<ul className='text-2xl mt-2 ml-7 searchnot'> No Group found</ul>) //To be changed in future

              }
            </div>) :
            (<div className='text-2xl mt-2 ml-7'> Intially Result</div>)}
        </div>
        {mscreen ? (
          <div className='h-screen text-2xl bg-gray-200 flex justify-center items-center' style={{ width: '55%' }}>
            <div>
              Start Messaging
            </div>
          </div>) :
          (<div className='h-screen' style={{ width: '55%' }} >
            <div className='bg-gray-500 pt-3 pl-10 text-3xl' style={{ height: '10%' }}>{senderGroupName}</div>
            <div className='bg-green-300 ' style={{ height: '80%' }} ></div>
            <div className='bg-gray-300' style={{ height: '10%' }}><input type="text" value={messagetext} onChange={e => messageBox(e)} onClick={e => messageBox(e, false)} name="" id="" className='inline-block ' style={{ width: '75%', paddingLeft: '1%', marginLeft: '10%', height: '70%', marginTop: '1%' }} /> <button style={{ width: '10%', height: '50%' }}><img src="/rightarrow.png" onClick={(e) => sendMessage(e)} style={{ width: "50%", height: '150%', paddingTop: '5%' }} /></button></div>
          </div>)}
      </div>
    </>
  );
}
