import { React, useState, useRef, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
const { io } = require("socket.io-client");


const socket = io.connect("http://localhost:5100")

export default function Homescreen() {
  const location = useLocation();


  const inputRef = useRef(null);

  const [groupMessages, setGroupMessage] = useState(false);

  const { username, password } = location.state || {};

  const [searchusersgroup, setSearchUsersgroup] = useState([]);

  const [searchusers, setSearchsUsers] = useState([]);
  const [searchgroupMessages, setsearchGroupMessages] = useState([]);

  const [searchbool, setSearchbool] = useState(false);

  const [mscreen, setMscreen] = useState(true);

  const [senderGroupName, setSenderGroupName] = useState("");

  const [currsendmessage, setCurrsendmessage] = useState("");


  const [addgroupModal, setAddgroupModal] = useState(false);


  const [messagetext, setmessagetext] = useState("Type message here");

  const [intial_user_group, setIntial_user_group] = useState([]);

  const [usersingroups, setUsersingroups] = useState([])


 
  const switchModal = () => {
    setAddgroupModal(!addgroupModal);
    let to_add_group = document.getElementById("to_add_group");
    if (addgroupModal) {
      console.log("If");
      to_add_group.classList.remove("invisible");
      to_add_group.classList.add("visible");
    } else {
      console.log("Else");
      to_add_group.classList.remove("visible");
      to_add_group.classList.add("invisible");
    }
  }


  const clickOnImage = (event) => {
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    if (a.nodeValue == '/leftArrow.png') {
      fetchintialusergroup();
      a.nodeValue = '/searchIcon.png';
      setSearchbool(false);
      setMscreen(true);
      setmessagetext("Type message here");
    }
    else {
      a.nodeValue = '/leftArrow.png';
      setSearchbool(true);

      if (inputRef.current) {
        inputRef.current.focus();
      }

    }
    event.stopPropagation();

  }

  const handleContainerClick = (event) => {
    const target = event.target;
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    const flag = target.classList.contains('searchnot');
  }

  const oninput = async (e) => {
    const value = e.target.value;
    setSearchbool(true);
    if (value.length > 0) {
      const response = await fetch('http://localhost:5010/tofetch', {
        method: 'POST',
        body: JSON.stringify({ "username": value }),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      const data = await response.json();
      const data_users = data.users
      const data_group = data.groupMessages;

      setSearchsUsers(data_users || []);
      setsearchGroupMessages(data_group || []);
    }
    else {
      setSearchsUsers([]);
      setsearchGroupMessages([]);
    }
  }

  useEffect(() => {
    const handleReceiveMessage = (data) => {
      // console.log("In the receive message");
      console.log(data);
      const currentDate = data.currentDate;
      const currentTime = data.currentTime;
      const sender = data.sender;
      const receiver = data.receiver;
      const textToSend = data.textToSend;

      if (currsendmessage == "") {
        setCurrsendmessage({
          "currentDate": currentDate,
          "currentTime": currentTime,
          "sender": sender,
          "receiver": receiver,
          "textToSend": textToSend
        });


        if (senderGroupName == receiver) {

          loadindimess(sender, receiver);
        }

      }
      else if (currsendmessage["currentTime"] != currentTime) {
        setCurrsendmessage({
          "currentDate": currentDate,
          "currentTime": currentTime,
          "sender": sender,
          "receiver": receiver,
          "textToSend": textToSend
        });
        if (senderGroupName == receiver) {

          loadindimess(sender, receiver);
        }
      }

    };

    socket.on("receive_message", handleReceiveMessage);

    return () => {
      socket.off("receive_message", handleReceiveMessage);
    };
  });



  const startindivimessage = async (e, u2) => {
    setMscreen(false);
    const receiver = username;
    const sender = u2;
    if (sender == receiver) {
      const t1 = receiver + ' (Self)';
      setSenderGroupName(t1);
      socket.emit("join_room", t1);
    }
    else {
      setSenderGroupName(sender);
      const identifier1 = receiver + '#' + sender;
      const identifier2 = sender + '#' + receiver;
      socket.emit("join_room", identifier1);
      socket.emit("join_room", identifier2);
    }
    setmessagetext("Type message here");
    loadindimess(receiver, sender);
  }
  const loadindimess = async (receiver, sender) => {


    const response = await fetch('http://localhost:5100/tofetchindichat', {
      method: 'POST',
      body: JSON.stringify({ "receiver": receiver, "sender": sender }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    let messageDisplayContainer = document.getElementById("messageDisplay");
    if (messageDisplayContainer != null)
      messageDisplayContainer.innerHTML = "";
    const result = await response.json();
    if (result.length == 0) {

      messageDisplayContainer.innerHTML = "";
      return;
    }

    result.sort((a, b) => {
      // Parse dates (currentDate is in DD/MM/YYYY format)
      const [dayA, monthA, yearA] = a.currentDate.split('/').map(Number);
      const [dayB, monthB, yearB] = b.currentDate.split('/').map(Number);

      // Compare years, months, days first
      if (yearA !== yearB) return yearA - yearB;
      if (monthA !== monthB) return monthA - monthB;
      if (dayA !== dayB) return dayA - dayB;

      // If the dates are the same, compare times
      const timeA = a.currentTime.split(':').map(Number); // [hours, minutes, seconds]
      const timeB = b.currentTime.split(':').map(Number);

      // Compare hours, minutes, seconds
      if (timeA[0] !== timeB[0]) return timeA[0] - timeB[0];
      if (timeA[1] !== timeB[1]) return timeA[1] - timeB[1];
      return timeA[2] - timeB[2];
    });

    let lastDate = "10/12/2024";

    result.forEach((currentEle) => {
      const { chat, currentDate, currentTime, receiver, sender, whichuser } = currentEle;

      // Add a date header if the date changes
      if (currentDate !== lastDate) {
        lastDate = currentDate;
        const dateHeader = document.createElement("h1"); // Change 'h' to 'h1'
        dateHeader.className = "flex justify-center items-center h-12"; // Flexbox for centering

        const dateContainer = document.createElement("span");
        dateContainer.className = "bg-red-700 border border-yellow-700 text-white px-4 py-2 rounded-md";
        dateContainer.innerText = currentDate;

        dateHeader.append(dateContainer);
        messageDisplayContainer.appendChild(dateHeader);
      }

      // Create a message bubble
      const messageBubble = document.createElement("div");
      messageBubble.className = `p-3 rounded-lg m-2 max-w-xs ${whichuser === receiver
        ? "bg-blue-500 text-white self-end ml-auto"
        : "bg-gray-200 text-black self-start mr-auto"
        }`;

      messageBubble.style.marginBottom = "1rem"; // This adds some space between messages

      // Create a wrapper to hold both the message and the time
      const messageWrapper = document.createElement("div");
      messageWrapper.className = "flex flex-col h-[7vh]"; // Stack the message and time vertically

      // Message text
      const messageText = document.createElement("p");
      messageText.className = "text-[1.3vw] mb-1"; // Add some bottom margin between message and time
      messageText.innerText = chat;

      // Message time
      const messageTime = document.createElement("span");
      messageTime.className = "text-[1vw] text-gray-600 self-end"; // Align time to the bottom-right
      messageTime.innerText = currentTime;

      // Append text and time to the wrapper
      messageWrapper.appendChild(messageText);
      messageWrapper.appendChild(messageTime);

      // Append the wrapper to the message bubble
      messageBubble.appendChild(messageWrapper);

      // Append bubble to container
      messageDisplayContainer.appendChild(messageBubble);
    });
    messageDisplayContainer.scrollTop = messageDisplayContainer.scrollHeight;


  }
    ;

  // When click on send message arrow
  const messageBox = async (e, flag = true) => {
    e.stopPropagation();
    if (flag == true) {
      if (messagetext == "Type message here") {
        const d = e.target.value;
        const i = d.indexOf('Type message here')
        const m1 = d.substring(i + ('Type message here'.length));
        setmessagetext(m1);
      }
      else if (e.target.value && e.target.value.length > 0) {

        setmessagetext(e.target.value);
      }
      else {

        setmessagetext("Type message here");
      }
    }
    else {
      if (messagetext == "Type message here") {
        e.target.value = "";
        setmessagetext(e.target.value);
      }
    }

  }

  const sendMessage = async (e) => {
    const textToSend = messagetext;
    const receiver = username;
    const sender = senderGroupName;

    if (textToSend.length == 0) {
      setmessagetext("Type message here");
      return;
    }
    if (groupMessages == false) {
      const identifier = receiver + ' (Self)';
      if (senderGroupName == identifier) {

        // Coming soon
        // const identifier1 = receiver + '#' + sender;
        // const identifier2 = sender + '#' + receiver;
        const now = new Date(); // Create a new Date object with the current date and time

        // Get the date components
        const day = now.getDate(); // Day of the month (1-31)
        const month = now.getMonth() + 1; // Month (0-11), so add 1 for 1-12
        const year = now.getFullYear(); // Full year (e.g., 2024)

        const hours = now.getHours(); // Hours (0-23)
        const minutes = now.getMinutes(); // Minutes (0-59)
        const seconds = now.getSeconds(); // Seconds (0-59)


        const currentDate = `${day}/${month}/${year}`;
        const currentTime = `${hours}:${minutes}:${seconds}`;

        setmessagetext("Type message here");
        const response = await fetch('http://localhost:5100/saveindichat', {
          method: 'POST',
          body: JSON.stringify({
            "receiver": receiver,
            "sender": receiver,
            "whichuser": receiver,
            "textToSend": textToSend,
            "currentDate": currentDate,
            "currentTime": currentTime
          }),
          headers: {
            'Content-Type': 'application/json'
          }
        })
        loadindimess(receiver, receiver);



      }
      else {
        const identifier1 = receiver + '#' + sender;
        const identifier2 = sender + '#' + receiver;
        const now = new Date(); // Create a new Date object with the current date and time

        // Get the date components
        const day = now.getDate(); // Day of the month (1-31)
        const month = now.getMonth() + 1; // Month (0-11), so add 1 for 1-12
        const year = now.getFullYear(); // Full year (e.g., 2024)

        const hours = now.getHours(); // Hours (0-23)
        const minutes = now.getMinutes(); // Minutes (0-59)
        const seconds = now.getSeconds(); // Seconds (0-59)


        const currentDate = `${day}/${month}/${year}`;
        const currentTime = `${hours}:${minutes}:${seconds}`;

        setmessagetext("Type message here");
        const response = await fetch('http://localhost:5100/saveindichat', {
          method: 'POST',
          body: JSON.stringify({
            "receiver": receiver,
            "sender": sender,
            "whichuser": receiver,
            "textToSend": textToSend,
            "currentDate": currentDate,
            "currentTime": currentTime
          }),
          headers: {
            'Content-Type': 'application/json'
          }
        })
        const result = await response.json();
        const response2 = await fetch('http://localhost:5100/saveindichat', {
          method: 'POST',
          body: JSON.stringify({
            "receiver": sender,
            "sender": receiver,
            "whichuser": receiver,
            "textToSend": textToSend,
            "currentDate": currentDate,
            "currentTime": currentTime
          }),
          headers: {
            'Content-Type': 'application/json'
          }
        })
        const result2 = await response2.json();
        socket.emit('send_message', { identifier1, identifier2, receiver, sender, textToSend, currentDate, currentTime });
        loadindimess(receiver, senderGroupName);

      }
    }
    else {
      //For group chat coming soon
    }

  }
  const submitNewGroup = async () => {
    let group_name = document.getElementById("group_name");
    const val = group_name.value;
    if (val.length == 0) {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "Enter Group Name";
      return;
    }
    else {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "";
    }

    if (usersingroups.length <= 1) {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "Atleast 2 users are required";
      return;
    }
    else {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "";
    }

    const response = await fetch('http://localhost:4000/signup', {
      method: 'POST',
      body: JSON.stringify({ "username": val }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const data = await response.json();
    const length = data.length;
    if (length == 1) {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "Group name matches with username";
      return;
    }



    const res = await fetch("http://localhost:5200/checkgroupexist", {
      method: 'POST',
      body: JSON.stringify({
        "groupname": val
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const res2 = await res.json();
    if (res2.flag == "true") {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "Group name already exists";
      return;
    }
    else {
      let display_res = document.getElementById("display_res");
      display_res.textContent = "";
    }
    let groupmember = [];
    groupmember = usersingroups.map((user) => user.username);
    groupmember.push(username);
    const now = new Date(); // Create a new Date object with the current date and time

    // Get the date components
    const day = now.getDate(); // Day of the month (1-31)
    const month = now.getMonth() + 1; // Month (0-11), so add 1 for 1-12
    const year = now.getFullYear(); // Full year (e.g., 2024)

    const hours = now.getHours(); // Hours (0-23)
    const minutes = now.getMinutes(); // Minutes (0-59)
    const seconds = now.getSeconds(); // Seconds (0-59)

    const currentDate = `${day}/${month}/${year}`;
    const currentTime = `${hours}:${minutes}:${seconds}`;
    const response2 = await fetch('http://localhost:5200/savegroupinfo', {
      method: 'POST',
      body: JSON.stringify({
        "receiver": username,
        "groupname": val,
        "groupmember": groupmember,
        "currentDate": currentDate,
        "currentTime": currentTime
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    let display_res = document.getElementById("display_res");
    display_res.textContent = "Group created successfully";
    setTimeout(() => {
      switchModal();
      setSearchUsersgroup([]);
      setUsersingroups([]);
    }, 2000);

  }

  const searchusersforgroup = async (e) => {
    const sender = e.target.value;

    if (sender.length > 0) {
      const response = await fetch('http://localhost:5010/tofetch', {
        method: 'POST',
        body: JSON.stringify({ "username": sender }),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      const data = await response.json();
      const data_users = data.users
      const data_group = data.groupMessages;

      setSearchUsersgroup(data_users || []);

    }
    else {
      setSearchUsersgroup([]);
    }
  }

  const addingroup = (e, user) => {
    e.preventDefault();
    const alreadyAdded = usersingroups.some((element) => element._id === user._id);
    if (alreadyAdded) return;

    setUsersingroups((prev) => [...prev, user]);

  }
  const removeingroup = (e, user) => {
    e.preventDefault();
    setUsersingroups((prev) => prev.filter((element) => element._id !== user._id))
  }

  const fetchintialusergroup = async () => {

    const res1 = await fetch('http://localhost:5100/intialindires', {
      method: 'POST',
      body: JSON.stringify({ "receiver": username }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const data1 = await res1.json();

    const res2 = await fetch("http://localhost:5200/intialresultgroup", {
      method: 'POST',
      body: JSON.stringify({
        "receiver": username
      }),
      headers: {
        'Content-Type': 'application/json'
      }
    })

    const data2 = await res2.json();


    setIntial_user_group( [...data1, ...data2]);

  }

  const make_entries=()=>{
    console.log(intial_user_group);
  }
  return (
    <>
      {addgroupModal &&
        <div className='bg-gray-300 fixed top-10 left-[25vw] h-[80vh] w-[50vw]'>
          <button onClick={switchModal} className='bg-red-500  hover:bg-red-600 rounded mt-[1vh] mr-[0.5vw] float-right text-[1.5vw] h-[8vh] w-[5vw]' >Close</button>
          <input type="text" id="group_name" name="" placeholder='Enter Group Name: ' className='relative top-4 left-4 w-[30vw] h-[6vh] p-2 border-black' />
          <input type="text" id="search_user" onChange={e => searchusersforgroup(e)} name="" placeholder='Search User: ' className='relative top-7 left-4 w-[30vw] h-[6vh] p-2 border-black' />
          <div className='bg-white h-[45vh] mt-[7vh] w-[23vw] ml-[1vw] float-left overflow-y-auto class="[&::-webkit-scrollbar]:hidden [-ms-overflow-style:none] [scrollbar-width:none]'>
            {searchusersgroup.length > 0 ? (
              <ul className='text-2xl mt-2  searchnot'>
                {searchusersgroup.map((user) => (user.username != username &&
                  <li key={user._id} style={{ width: '90%', marginLeft: '5%' }} className='border-8 cursor-pointer mt-2 border-gray-700 pl-2  h-[9vh]  w-88  searchnot text-[2vw]'>{user.username} <button type="button" onClick={e => addingroup(e, user)} className='bg-green-300 hover:bg-green-600 h-[5vh] float-right mt-1 mr-1 rounded w-[6vw]'>ADD</button> </li>
                ))}
              </ul>
            ) : (<ul className='text-[1.8vw] mt-2 ml-7 searchnot'> No Users found</ul>)}
          </div>
          <div className='bg-white h-[45vh] mt-[7vh] w-[23vw] mr-[1vw] float-right'>
            {usersingroups.length > 0 ? (
              <ul className='text-2xl mt-2  searchnot'>
                {usersingroups.map((user) => (
                  <li key={user._id} style={{ width: '90%', marginLeft: '5%' }} className='border-8 cursor-pointer mt-2 border-gray-700 pl-2  h-[9vh]  w-88  searchnot text-[2vw]'>{user.username} <button type="button" onClick={e => removeingroup(e, user)} className='bg-red-500 hover:bg-red-600 h-[5vh] float-right mt-1 mr-1 rounded w-[8vw]'>Remove</button> </li>
                ))}
              </ul>
            ) : (<ul className='text-[1.8vw] mt-2 ml-7 searchnot'> No Users is added</ul>)}
          </div>
          <div id='display_res' className='text-red-500 h-[5vh] mt-[54vh] mx-auto w-[30vw] text-center'></div>
          <button className='bg-green-500  hover:bg-green-600 rounded ml-[20vw]  mt-[1vh] text-[1.5vw] h-[6vh] w-[8vw]' onClick={e => submitNewGroup(e)} >Submit</button>
        </div>}
      <div className='flex flex-row h-screen' onClick={handleContainerClick} >
        <div className=' bg-gray-400' style={{ width: '5%', height: '100%' }}>

        </div>
        <div style={{ width: '40%' }}>
          <div className='bg-fixed'>
            <span className='text-[3vw] font-bold relative top-3 pl-[2vw]'>
              Chats
            </span>

            <button type="button" id="to_add_group" className='relative left-[13vw] text-[2vw] top-3 font-bold border-gray-500 rounded-lg border-[0.5vw] border-solid visible' onClick={switchModal}>New Group Chat</button>


            <br />
            <div className='mt-10 flex items-center' id='search_container' style={{ marginLeft: '5%' }}>
              <span className='rounded-l-md bg-gray-200 h-10 pl-3 hover:cursor-pointer' onClick={(e) => clickOnImage(e)}><img src="/searchIcon.png" alt="" style={{ height: '30px', width: '25px' }} id='search_image' className='inline-block h-10 w-3 pt-2' /></span>
              <input type="text" ref={inputRef} placeholder='Search' id="search_user" className=' h-10 rounded-r-md inline bg-gray-200' onChange={(e) => oninput(e)}
                style={{ outline: 'None', width: '90%', paddingLeft: '10%', marginRight: '5%' }} onClick={(e) => clickOnImage(e)} />
            </div>
          </div>
          {searchbool ? (
            <div id='search_panel' className='searchnot overflow-auto' style={{ maxHeight: '480px', overflowY: 'auto' }}>

              {searchusers.length > 0 ? (
                <ul className='text-2xl mt-2  searchnot'>
                  <span className='ml-7 searchnot'>Users:</span>
                  {searchusers.map((user) => (
                    <li key={user._id} onClick={(e) => startindivimessage(e, user.username)} style={{ width: '90%', marginLeft: '5%' }} className='border-8 cursor-pointer mt-2 border-gray-700 pl-2  h-20  w-88  searchnot text-[2vw]'>{user.username}</li>
                  ))}
                </ul>
              ) : (
                <ul className='text-[2vw] mt-2 ml-7 searchnot'> No Users found</ul>)}

              {searchgroupMessages.length > 0 ? (
                <ul className='text-2xl mt-2 ml-7 searchnot'>
                  <span className='ml-7 searchnot'> Group: </span>
                  {searchusers.map((group) => (
                    <li key={group._id} style={{ width: '82%' }} className='border-8 mt-2 border-gray-700 pl-2 searchnot'>{group.groupname}</li>
                  ))}
                </ul>) :
                (<ul className='text-[2vw] mt-2 ml-7 searchnot'> No Group found</ul>) //To be changed in future

              }
            </div>) :
            (<div className='text-[2vw] mt-2 ml-7'> {make_entries()} </div>)}
        </div>
        {mscreen ? (
          <div className='h-screen text-[2vw] bg-gray-200 flex justify-center items-center' style={{ width: '55%' }}>
            <div>
              Start Messaging
            </div>
          </div>) :
          (<div className='h-screen' style={{ width: '55%' }} >
            <div className='bg-gray-500 pt-1 pl-10 text-[3vw]' style={{ height: '10%' }}>{senderGroupName}</div>
            <div className='bg-gray-300 flex-1 overflow-y-auto' id="messageDisplay" style={{ height: '80%' }} ></div>
            <div className='bg-red-300 ' style={{ height: '10%' }}><input type="text" value={messagetext} onChange={e => messageBox(e)} onClick={e => messageBox(e, false)} name="" id="" className='inline-block ' style={{ width: '75%', paddingLeft: '1%', marginLeft: '10%', height: '70%', marginTop: '1%' }} /> <button style={{ width: '10%', height: '50%' }}><img src="/rightarrow.png" onClick={(e) => sendMessage(e)} style={{ width: "50%", height: '150%', paddingTop: '5%' }} /></button></div>
          </div>)}
      </div>

    </>
  );
}
