import { React, useState, useRef, useEffect } from 'react';
import Users from './components/Users';
import Chat from './components/Chat';
import Leftbar from './components/Leftbar';
import { useLocation } from 'react-router-dom';


export default function Homescreen() {
  const location = useLocation();

  const [LeftArrow, setLeftArrow] = useState('false')

  const inputRef = useRef(null);

  const clickOnImage = (event) => {
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    if (a.nodeValue == '/leftArrow.png') {
      console.log("if");
      a.nodeValue = '/searchIcon.png';
    }
    else {
      console.log("else");
      a.nodeValue = '/leftArrow.png';
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }
    event.stopPropagation();

  }
  const handleContainerClick = (event) => {
    const target = event.target;
    console.log("Handle Container function");
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    if (a.nodeValue === '/leftArrow.png' && target.id !== 'search_container') {
      clickOnImage(event); 
    }
  }

  useEffect(async () => {
    let user_name_container=document.getElementById('user_name_container');
    const response=await fetch('http://localhost:5001/tofetch',{
      method:'POST',      
      body:JSON.stringify({"username":username,"password":password}),
      headers:{
        'Content-Type':'application/json'
      }
    })
    const data=await response.json();
    if(data.length==1)
    {
      let ele=document.getElementById('result');
      ele.textContent="Login successful"
      setTimeout(()=>{
        navigate('/Homescreen',{state:{username:username,password:password}});
      },1000);
    }
    else{
      let ele=document.getElementById('result');
      ele.textContent="Incorrect username or password."
    }


  }, []);

  const { username, password } = location.state || {};
  const [Showchat, setShowchat] = useState(false)
  return (
    <>
      <div className='flex flex-row h-screen' onClick={handleContainerClick} >
        <div className=' bg-gray-400 h-screen' style={{ width: '5%' }}>

        </div>
        <div style={{ width: '40%' }}>
          <div className='bg-fixed'>
            <span className='text-3xl font-bold relative top-3 pl-7'>
              Chats
            </span>
            <br />
            <div className='mt-10 ml-5 flex items-center' id='search_container' >
              <span className='rounded-l-md bg-gray-200 h-10 pl-3 hover:cursor-pointer' onClick={(e) => clickOnImage(e)}><img src="/searchIcon.png" alt="" srcset="" style={{ height: '30px', width: '25px' }} id='search_image' className='inline-block h-10 w-3 pt-2' /></span>
              <input type="text" ref={inputRef} placeholder='Search' className=' h-10 rounded-r-md inline w-98 bg-gray-200 pl-10'
                style={{ outline: 'None' }} onClick={(e) => clickOnImage(e)} />
              <div className="users_name" id='user_name_container'>

              </div>
            </div>
          </div>
        </div>
        <div className='h-screen text-2xl bg-gray-200 flex justify-center items-center' style={{ width: '55%' }}>
          <div>
            Start Messaging
          </div>
        </div>
      </div>
    </>
  );
}
