import './App.css';
import { React } from 'react';
import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");


  const navigate = useNavigate();

  const callSubmit = async (e) => {
    e.preventDefault();
    if (username.length == 0 || password.length == 0) {
      let ele = document.getElementById('result');
      ele.textContent = "UserName or Password is empty"
      return;
    }
    else {
      let ele = document.getElementById('result');
      ele.textContent = "";
    }
    const response = await fetch('http://localhost:5000/login', {
      method: 'POST',
      body: JSON.stringify({ "username": username, "password": password }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const data = await response.json();
    if (data.length == 1) {
      let ele = document.getElementById('result');
      ele.textContent = "Login successful"
      setTimeout(() => {
        navigate('/Homescreen', { state: { "username": username, "password": password } });
      }, 1000);
    }
    else {
      let ele = document.getElementById('result');
      ele.textContent = "Incorrect username or password."
    }

  }
  return (
    <>
      <div className="flex items-center justify-center h-screen">
        <div className="border-4  border-black h-[70vh] w-[25vw] bg-yellow-400 ">
          Chatty is built using the frontend React.js. Tailwind CSS used for styling and creating responsive layout. On the backend Express handles client-server operation. MongoDB for storing schema user, individual messages and group messages and socket.io for real-time communication.
        </div>
        <div className='bg-green-600'>
          <form action="" id="form_login" className='border-4  border-black h-[70vh] w-[25vw]'>
            <label htmlFor="name_id" className='ml-[2vw]'>Username</label> <br />
            <input required="true" className='h-[6vh] ml-[2vw] mt-4 border-4 border-gray w-[20vw] rounded-md ' type="text" id="name_id" placeholder={username.length == 0 ? `Enter your Username` : username} onChange={(e) => setUsername(e.target.value)} />
            <br />
            <label htmlFor="number_id" className='ml-10 top-4 relative' >Password</label> <br />
            <input required="true" className='h-[6vh] ml-10 mt-4 border-4 border-gray w-[20vw] ' type="password" name="number_name" id="number_id" placeholder={password.length == 0 ? `Enter your Password` : password} onChange={(e) => setPassword(e.target.value)} />
            <br />
            <button className="bg-blue-500 hover:bg-blue-600 mt-4 ml-10 w-72 h-10" onClick={(e) => callSubmit(e)}>Log In</button>
            <p className='mt-4 ml-12'>Don't have an account?<Link to="/SignUp" className='underline'>Register Here</Link></p>
            <p id='result' className="mt-4 text-red-600 flex justify-center"></p>

          </form>
        </div>

      </div>
    </>
  );
}

export default Login;
