import './App.css';
import { React} from 'react';
import { Link } from 'react-router-dom';
import {useState} from 'react';


function SignUp() {
  const [username,setUsername]=useState("");
  const [password,setPassword]=useState("");
  const [repassword,setRePassword]=useState("");
 
  const submitData=async(e)=>{
    e.preventDefault();
    if(password!=repassword)
    {
        const result=document.getElementById("result")
    }
    const response=await fetch('http://localhost:3000/SignUp',{
      method:'POST',
      body:JSON.stringify(username,password),
      headers:{
        'Content-Type':'application/json'
      }
    });

    console.log(response);
  }
  return (
    <>
      <div className="h-40 my-3 flex justify-center">
        <form method="post">
          <fieldset className='border-4 border-double border-purple-900 h-100 w-96'>
            <legend className='flex mx-auto'>Sign Up DETAILS</legend>
            <label htmlFor="name_id" className='ml-10 relative top-4'>Username</label> <br />
            <input className='h-10 ml-10 mt-4 border-4 border-gray w-72 rounded-md ' required="required" type="text" id="name_id" placeholder={username.length==0?`Enter your Username`:username} onChange={(e)=>setUsername(e.target.value)} />
            <br />
            <label htmlFor="password_id" className='ml-10 top-4 relative' >Password</label> <br />
            <input className='h-10 ml-10 mt-4 border-4 border-gray w-72' required="required" type="password" name="password_name" id="password_id" placeholder={password.length==0?`Enter your Password`:password} onChange={(e)=>setPassword(e.target.value)}/>
            <label htmlFor="re-password_id" className='ml-10 top-4 relative' >Re-Password</label> <br />
            <input className='h-10 ml-10 mt-4 border-4 border-gray w-72' required="required" type="password" name="re-password_name" id="re-password_id" placeholder={repassword.length==0?`Enter your Password`:repassword} onChange={(e)=>setRePassword(e.target.value)} />
            <br />
            <button onClick={submitData}  className="bg-green-700 hover:bg-green-400 mt-4 ml-10 w-72 h-10">Sign Up</button>
            <p className='mt-4 ml-18 '>Already have an account?<Link to="/" className='underline'>Login</Link></p>
            <p className='result'></p>
          </fieldset>
        </form>
      </div>
    </>
  );
}

export default SignUp;
