import './App.css';
import { React } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';


function SignUp() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [repassword, setRePassword] = useState("");

  const navigate=useNavigate();

  const submitData = async (e) => {
    e.preventDefault();
    if (password != repassword) {
      const result = document.getElementById("result")
      result.textContent = "Password and Re Enter Password not match"
      return;
    }
    if(username.length==0)
    {
      const result = document.getElementById("result")
      result.textContent = "Username is empty"
      return;
    }
    else {
      const result = document.getElementById("result")
      result.textContent = ""
    }
    const response = await fetch('http://localhost:4000/signup', {
      method: 'POST',
      body: JSON.stringify({ "username": username }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
    const data = await response.json();
    const length = data.length;
    if (length == 1) {
      const result = document.getElementById("result")
      result.className = "text-red-600 ml-12";
      result.textContent = "UserName already exists. Try different!";
    } else {
      const result = document.getElementById("result");
      result.className = "text-red-600 ml-24";
      result.textContent = "Details saved successfully";
      const response = await fetch('http://localhost:4000/signupnow', {
        method: 'POST',
        body: JSON.stringify({ "username": username,"password":password }),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      setTimeout(()=>{
        navigate('/Homescreen',{state:{username:username,password:password}});
      },1000);
    }
  }
  return (
    <>
      <div className="my-3 flex justify-center">
        <form method="post">
          <fieldset className='border-4 border-double border-purple-900 w-96 h-100'>
            <legend className='flex mx-auto'>SIGN UP DETAILS</legend>
            <label htmlFor="name_id" className='ml-10 relative top-4'>Username</label> <br />
            <input className='h-10 ml-10 mt-4 border-4 border-gray w-72 rounded-md ' required="required" type="text" id="name_id" placeholder={username.length == 0 ? `Enter your Username` : username} onChange={(e) => setUsername(e.target.value)} />
            <br />
            <label htmlFor="password_id" className='ml-10 top-4 relative' >Password</label> <br />
            <input className='h-10 ml-10 mt-4 border-4 border-gray w-72' required="required" type="password" name="password_name" id="password_id" placeholder={password.length == 0 ? `Enter your Password` : password} onChange={(e) => setPassword(e.target.value)} />
            <label htmlFor="re-password_id" className='ml-10 top-4 relative' >Re-Password</label> <br />
            <input className='h-10 ml-10 mt-4 border-4 border-gray w-72' required="required" type="password" name="re-password_name" id="re-password_id" placeholder={repassword.length == 0 ? `Enter your Password` : repassword} onChange={(e) => setRePassword(e.target.value)} />
            <br />
            <button onClick={(e) => submitData(e)} className="bg-green-700 hover:bg-green-400 mt-4 ml-10 w-72 h-10">Sign Up</button>
            <p className='mt-4 ml-18 '>Already have an account?<Link to="/" className='underline'>Login</Link></p>
            <p id='result' className='text-red-600 ml-3 flex justify-center'></p>
          </fieldset>
        </form>
      </div>
    </>
  );
}

export default SignUp;
