        <div style={{ width: '40%', height: "100%" }}>
          <div className='bg-fixed'>
            <span className='text-[2vw] font-bold relative top-3 pl-[2vw]'>
              Chats
            </span>

            <button type="button" id="to_add_group" className='relative left-[13vw] text-[2vw] top-3 font-bold border-gray-500 rounded-lg border-[0.5vw] border-solid visible' onClick={switchModal}>New Group Chat</button>


            <br />
            <div className='mt-10 flex items-center' id='search_container' style={{ marginLeft: '5%' }}>
              <span className='rounded-l-md bg-gray-200 h-[10vh] pl-[3vw] hover:cursor-pointer ' onClick={(e) => clickOnImage(e)}><img src="/searchIcon.png" alt="" style={{ height: '6vh', width: '2vw' }} id='search_image' className='inline-block h-10 w-3 pt-2' /></span>
              <input type="text" ref={inputRef} placeholder='Search' id="search_user" className=' h-[10vh] rounded-r-md inline bg-gray-200' onChange={(e) => oninput(e)}
                style={{ outline: 'None', width: '90%', paddingLeft: '10%', marginRight: '5%' }} onClick={(e) => clickOnImage(e)} />
            </div>
          </div>
          {searchbool ? (
            <div id='search_panel' className='searchnot overflow-auto' style={{ maxHeight: '480px', overflowY: 'auto' }}>

              {searchusers.length > 0 ? (
                <ul className='text-2xl mt-2  searchnot'>
                  <span className='ml-[2.2vw] text-[2vw] searchnot'>Search User</span>
                  {searchusers.map((user) => (
                    <li key={user._id} onClick={(e) => startindivimessage(e, user.username)} className='border-8 cursor-pointer mt-[2vh] ml-[2vw] border-gray-700 pl-[1vw] pt-[1vh]  h-[10vh]  w-[36vw]  searchnot text-[2vw]'>{user.username}</li>
                  ))}
                </ul>
              ) : <div className='ml-[2.2vw] mt-[0.5vh] text-[2vw] searchnot'>Search User not Found</div>}
            </div>) :
            (<div className='overflow-auto h-[78vh] class="[&::-webkit-scrollbar]:hidden [-ms-overflow-style:none] [scrollbar-width:none]'  >
              {intial_user_group.length > 0 && (
                <ul className=''>
                  <div >
                    {intial_user_group.map((item, index) => (
                      <li key={index} className='border-4 cursor-pointer mt-[2vh] ml-[2vw] border-gray-700 pl-[1vw] pt-[1vh]   w-[36vw]  searchnot text-[2vw]'>

                        {item.groupname ? (
                          <div className='h-[15vh] relative' onClick={(e) => startgroupmessage(e, item.groupname)} rig>
                            <p className="text-[3vh] font-bold text-blue-800">{item.groupname}</p>
                            <p className="text-[2vh] text-gray-600 overflow-hidden text-ellipsis whitespace-nowrap">Message: {item.chat}</p>
                            <p className="text-[2vh] text-gray-600 float-right mr-2">Date: {item.currentDate}</p>
                            <p className="text-[2vh] text-gray-600 float-right absolute bottom-0 right-2">Time: {item.currentTime}</p>
                          </div>
                        ) : (

                          <div onClick={(e) => startindivimessage(e, item.sender)} className='h-[15vh] relative'>
                            <p className="text-[3vh] font-bold text-green-800">From: {item.sender}</p>
                            <p className="text-[2vh] text-gray-600 overflow-hidden text-ellipsis whitespace-nowrap">Message: {item.latestMessage}</p>
                            <p className="text-[2vh] text-gray-600 float-right mr-2">Date: {item.currentDate}</p>
                            <p className="text-[2vh] text-gray-600 float-right absolute bottom-0  right-2">Time: {item.currentTime}</p>
                          </div>
                        )}
                      </li>
                    ))}
                  </div>
                </ul>
              )}

            </div>)}
        </div>
        {mscreen ? (
          <div className='h-screen text-[2vw] bg-gray-200 flex justify-center items-center' style={{ width: '55%' }}>
            <div>
              Start Messaging
            </div>
          </div>) :
          (<div className='h-screen' style={{ width: '55%' }} >
            <div className="bg-gray-500 pl-10 text-[6vh]" style={{ height: '10%' }}>
              <div className="flex justify-between items-center w-full">
                <span className="text-[3vw]">{senderGroupName}</span>
                <span className="inline-block mr-2 mb-2">
                  <button onClick={toggleExit} className="bg-green-400 h-[8vh] w-[10vw] text-[2vw] border-black rounded-md hover:bg-green-600 border-[0.2vw]">
                    Exit {groupMessages === true ? "Group" : "Chat"}
                  </button>
                </span>
              </div>
            </div>

            <div className='bg-gray-300 flex-1 overflow-y-auto' id="messageDisplay" style={{ height: '80%' }} ></div>
            <div className='bg-red-300 ' style={{ height: '10%' }}><input type="text" value={messagetext} onChange={e => messageBox(e)} onClick={e => messageBox(e, false)} name="" id="" className='inline-block ' style={{ width: '75%', paddingLeft: '1%', marginLeft: '10%', height: '70%', marginTop: '1%' }} /> <button style={{ width: '10%', height: '50%' }}><img src="/rightarrow.png" onClick={groupMessages == false ? (e) => sendMessage(e) : (e) => sendGroupmessage(e)} style={{ width: "50%", height: '150%', paddingTop: '5%' }} /></button></div>
          </div>)}
      </div>
      {deleleCHAT && (
        <div className="modal-background fixed top-0 left-0 w-full h-full z-10 bg-black bg-opacity-50" />
      )}
      {deleleCHAT &&
        <div className='bg-gray-300 fixed top-[30vh] left-[25vw] h-[30vh] w-[50vw] z-20 '>
          <div className='text-red-500 h-[5vh] text-[1.4vw] mx-auto w-[30vw] text-center'>Are you sure you want to delete that chat?</div>
          <div className='text-red-500 h-[5vh] text-[1.4vw] w-[50vw] text-center '>{groupMessages == true ? "On deleteting you will exit the group" : "On deleting message user will be delete from your homelist"}</div>
          <div id="delete_result" className='h-[5vh] text-green-500 mt-[1vh] text-[1.5vw] text-center'></div>
          <div className='mt-[1vh]'><button className='bg-green-500 hover:bg-green-600 h-[9vh] rounded-xl border-4 border-black w-[15vw] ml-[3vw]' onClick={deletechat}>Yes</button><button onClick={toggleExit} className='bg-red-500 hover:bg-red-700 border-4 border-black h-[9vh] w-[15vw] rounded-xl float-right mr-[3vw]'>No</button></div>

        </div>}

    </>
  );
}
