import { React, useState } from 'react';
import Users from './components/Users';
import Chat from './components/Chat';
import Leftbar from './components/Leftbar';
import { useLocation } from 'react-router-dom';


export default function Homescreen() {
  const location=useLocation() ;

  const [LeftArrow,setLeftArrow]=useState('false')

  const [defaultstyle,setdefaultStyle]=useState({
    width:'97%',
      backgroundImage:`url('/searchIcon.png')`,
      // backgroundRepeat:'no-repeat',
      // // backgroundPosition: '10px center',  // Adjust the background image position
      // backgroundSize: '20px',  // Adjust the size of the background image

  })

  const handleClick=()=>{
    setdefaultStyle({
      width:'97%',
      backgroundImage:`url('/leftArrow.jpg')`,
      backgroundRepeat:'no-repeat',
      // backgroundPosition: '10px center',  // Adjust the background image position
      backgroundSize: '20px',  // Adjust the size of the background image
    })
  }
     

  const {username,password}=location.state || {};
  const [Showchat,setShowchat]=useState(false)
  return (
    <>
    <div className='flex flex-row h-screen' >
      <div className=' bg-gray-400 h-screen' style={{width:'5%'}}>
      
      </div>    
      <div  style={{width:'40%'}}>
          <div className='bg-fixed'>
          <span className='text-3xl font-bold relative top-3 pl-7'>
            Chats
          </span>
          <br />
          <div className='mt-10' onClick={handleClick}>
            <div style={defaultstyle}></div>
            <p>
            <input type="text" placeholder='Search'  className='ml-20 inline-block h-10 rounded-md  bg-center bg-gray-200 w-98' 
            style={{outline:'None'}}/>
            </p>
          </div>
        </div>  
      </div>
      <div className='h-screen text-2xl bg-gray-200 flex justify-center items-center' style={{width:'55%'}}>
        <div>
          Start Messaging
        </div>
      </div>
    </div>
    </>
  );
}
