import { React, useState } from 'react';
import Users from './components/Users';
import Chat from './components/Chat';
import Leftbar from './components/Leftbar';
import { useLocation } from 'react-router-dom';


export default function Homescreen() {
  const location=useLocation() ;

  const {username,password}=location.state || {};
  const [Showchat,setShowchat]=useState(false)
  return (
    <>
    <div>
    <Leftbar></Leftbar>
    <div className='w-120 bg-green-600 h-full'>
        <span className='bg-fixed'>
          <span className='text-3xl font-bold relative top-3 pl-7'>
          Chats
          </span>
          <br />
          <span className='relative top-9 left-3'>
            <input type="text" placeholder='Search' className='inline-block h-9 rounded-md w-100 pl-20 bg-center' 
            style={{backgroundImage:`url('../../public/searchIcon.jpg')`}}/>
          </span>
        </span>
      </div>
    {Showchat==true?
    (  <div className='h-screen w-192 bg-gray-200 float-right flex justify-center items-center'></div> ):
    ( <div className='h-screen w-192 bg-gray-200 float-right flex justify-center items-center'><span className='text-2xl'>Start Messaging</span></div> )}
    </div>
    </>
  );
}
