import { React, useState, useRef, useEffect } from 'react';
import Users from './components/Users';
import Chat from './components/Chat';
import Leftbar from './components/Leftbar';
import { useLocation } from 'react-router-dom';


export default function Homescreen() {
  const location = useLocation();

  const [LeftArrow, setLeftArrow] = useState('false')

  const inputRef = useRef(null);

  const { username, password } = location.state || {};
  const [Showchat, setShowchat] = useState(false)

  const [searchusers, setSearchsUsers] = useState([]);
  const [searchgroupMessages, setsearchGroupMessages] = useState([]);

  const [searchbool,setSearchbool]=useState(false);

  const clickOnImage = (event) => {
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    if (a.nodeValue == '/leftArrow.png') {
      a.nodeValue = '/searchIcon.png';
      setSearchbool(false);
    }
    else {
      console.log("else");
      a.nodeValue = '/leftArrow.png';
      setSearchbool(true);
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }
    event.stopPropagation();

  }
  const handleContainerClick = (event) => {
    const target = event.target;
    console.log("Handle Container function");
    const searchimage = document.getElementById('search_image');
    let a = searchimage.attributes['src']
    console.log(target.id);
    let d=document.getElementById("search_panel");
    console.log(target.id);
    if (a.nodeValue === '/leftArrow.png' && target.id !== 'search_container' && target.id!=="search_panel") {
      clickOnImage(event);
    }
  }

  const oninput=async(e)=>{
    const value=e.target.value;
    setSearchbool(true);
    if(value.length>0){
    const response=await fetch('http://localhost:5010/tofetch',{
      method:'POST',      
      body:JSON.stringify({"username":value}),
      headers:{
        'Content-Type':'application/json'
      }
    })
    const data=await response.json();
    const data_users=data.users
    const data_group=data.groupMessages;

    setSearchsUsers(data_users || []);
    setsearchGroupMessages(data_group || []);
  }
  }

  useEffect(() => {
    // const fetchData=async()=> {
    //   let user_name_container = document.getElementById('user_name_container');
    //   const response = await fetch('http://localhost:5001/tofetch', {
    //     method: 'POST',
    //     body: JSON.stringify({ "username": username, "password": password }),
    //     headers: {
    //       'Content-Type': 'application/json'
    //     }
       
    //   })
    //   const data = await response.json();
    // }
    // const f1=fetchData();


  }, []);

  return (
    <>
      <div className='flex flex-row h-screen' onClick={handleContainerClick} >
        <div className=' bg-gray-400 h-screen' style={{ width: '5%' }}>

        </div>
        <div style={{ width: '40%' }}>
          <div className='bg-fixed'>
            <span className='text-3xl font-bold relative top-3 pl-7'>
              Chats
            </span>
            <br />
            <div className='mt-10 ml-5 flex items-center' id='search_container' >
              <span className='rounded-l-md bg-gray-200 h-10 pl-3 hover:cursor-pointer' onClick={(e) => clickOnImage(e)}><img src="/searchIcon.png" alt="" style={{ height: '30px', width: '25px' }} id='search_image' className='inline-block h-10 w-3 pt-2' /></span>
              <input type="text" ref={inputRef} placeholder='Search' className=' h-10 rounded-r-md inline w-98 bg-gray-200 pl-10' onChange={(e)=>oninput(e)}
                style={{ outline: 'None' }} onClick={(e) => clickOnImage(e)} />
            </div>
          </div>
          {searchbool?(
          <div id='search_panel' className='bg-gray-700'>    
            {searchusers.length>0?(
            <ul className='text-2xl mt-2 ml-7'>
            Users:
              {searchusers.map((user)=>(
                <li key={user._id}>{user.username}</li>
            ))}
            </ul>
            ):(
            <ul className='text-2xl mt-2 ml-7'> No users found</ul>)}
  
            {searchgroupMessages.length>0?(
            <ul className='text-2xl mt-2 ml-7'>
            Group:
              {searchusers.map((group)=>(
                <li key={group._id}>{group.groupname}</li>
            ))}
            </ul>):
            (<ul className='text-2xl mt-2 ml-7'> No Group found</ul>) //To be changed in future

            }
            </div>):
            (<div className='text-2xl mt-2 ml-7'> Intially Result</div>)}
        </div>
        <div className='h-screen text-2xl bg-gray-200 flex justify-center items-center' style={{ width: '55%' }}>
          <div>
            Start Messaging
          </div>
        </div>
      </div>
    </>
  );
}
