import './App.css';
import { React } from 'react';
import { useState } from 'react';
import {Link,useNavigate} from 'react-router-dom';

function Login() {
  const [username,setUsername]=useState("");
  const [password,setPassword]=useState("");


  const navigate=useNavigate();

  const callSubmit= async (e)=>{
    e.preventDefault();
    if(username.length==0 || password.length==0)
    {
        let ele=document.getElementById('result');
        ele.textContent="UserName or Password is empty"
        return;
    }
    else{
        let ele=document.getElementById('result');
        ele.textContent="";
    }
    const response=await fetch('http://localhost:5000/login',{
      method:'POST',      
      body:JSON.stringify({"username":username,"password":password}),
      headers:{
        'Content-Type':'application/json'
      }
    })
    const data=await response.json();
    if(data.length==1)
    {
      let ele=document.getElementById('result');
      ele.textContent="Login successful"
      setTimeout(()=>{
        navigate('/Homescreen',{state:{"username":username,"password":password}});
      },1000);
    }
    else{
      let ele=document.getElementById('result');
      ele.textContent="Incorrect username or password."
    }

  }
  return (
    <>
      <div className="h-40 my-3 flex justify-center bg-v">
        <form action="" id="form_login">
          <fieldset className='border-4 border-double border-purple-900 h-96 w-96'>
            <legend className='flex mx-auto'>LOGIN DETAILS</legend>
            <label htmlFor="name_id" className='ml-10 relative top-4'>Username</label> <br />
            <input required="true" className='h-10 ml-10 mt-4 border-4 border-gray w-72 rounded-md '  type="text" id="name_id" placeholder={username.length==0?`Enter your Username`:username} onChange={(e)=>setUsername(e.target.value)} />
            <br />
            <label htmlFor="number_id" className='ml-10 top-4 relative' >Password</label> <br />
            <input required="true" className='h-10 ml-10 mt-4 border-4 border-gray w-72'  type="password" name="number_name" id="number_id" placeholder={password.length==0?`Enter your Password`:password} onChange={(e)=>setPassword(e.target.value)} />
            <br />
            <button className="bg-blue-500 hover:bg-blue-600 mt-4 ml-10 w-72 h-10" onClick={(e)=>callSubmit(e)}>Log In</button>
            <p className='mt-4 ml-12'>Don't have an account?<Link to="/SignUp" className='underline'>Register Here</Link></p>
            <p id='result' className="mt-4 text-red-600 flex justify-center"></p>
          </fieldset>
        </form>
      </div>
    </>
  );
}

export default Login;
